// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "./Interfaces/IContractB.sol"; // Import the interface

contract ContractA {
    event EventA(uint indexed _value, address indexed _address, bool _flag);

    IContractB public contractB;

    constructor(address _contractB) {
        contractB = IContractB(_contractB);
    }

    function triggerEvents(uint _value, address _address, bool _flag) public {
        emit EventA(_value, _address, _flag); // Emit event for Contract A
        contractB.triggerEventB(_value, _address, _flag); // Call Contract B via its interface
    }
}
