// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

contract ContractB {
    event EventB(uint indexed _value, address indexed _address, bool _flag);

    function triggerEventB(uint _value, address _address, bool _flag) public {
        emit EventB(_value, _address, _flag);
    }
}
